import { Component, EventEmitter, Output } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private authService: AuthService) {}

  @Output() closeModalEvent = new EventEmitter<void>();
  @Output() openRegisterEvent = new EventEmitter<void>();
  @Output() loginSuccessEvent = new EventEmitter<void>();

  login(email: string, password: string) {       
    const success = this.authService.login(email, password);
    if (success) {
      this.loginSuccessEvent.emit();
      this.closeModalEvent.emit();
    } else {
      alert('Hatalı email veya şifre!');
    }
  }

  closeLoginModal() {
    this.closeModalEvent.emit();
  }

  openRegister() {
    this.openRegisterEvent.emit();
  }
}
<div class="modal fade" [class.show]="true" [style.display]="'block'">
    <div class="modal-dialog">
        <div class="modal-content">
            <div class="modal-header">
                <h5 class="modal-title">Login</h5>
                <button type="button" class="btn-close" (click)="closeLoginModal()"></button>
            </div>
            <div class="modal-body">
                <form #loginForm="ngForm" (ngSubmit)="login(email.value, password.value)">
                    <div class="mb-3">
                        <label for="email" class="form-label">Email</label>
                        <input type="email" class="form-control" id="email" name="email" #email="ngModel" required ngModel>
                        <div class="text-danger" *ngIf="email.invalid && (email.dirty || email.touched)">
                            Email gerekli
                        </div>
                    </div>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" class="form-control" id="password" name="password" #password="ngModel" required minlength="6" ngModel>
                        <div class="text-danger" *ngIf="password.invalid && (password.dirty || password.touched)">
                            Şifre en az 6 karakter olmalı
                        </div>
                    </div>
                    <button type="submit" class="btn btn-warning" [disabled]="loginForm.invalid">Login</button>
                    <p class="mt-2 mb-2">Dont Have an account? <a style="cursor: pointer;" (click)="openRegister()">Register Now</a></p>
                </form>
            </div>
        </div>
    </div>
</div>
