import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GetDataService } from '../service/get-data.service';
import { DataStorageService } from '../service/data-storage.service';
import { AuthService } from '../auth.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})
export class ProductDetailsComponent implements OnInit {
  commentForm!:FormGroup;

  constructor(private route: ActivatedRoute, private getData: GetDataService, private dataStorage: DataStorageService,
    private router: Router, private authService: AuthService ,  private fb: FormBuilder ) {

    }


  getParamValue: any;
  getProductDetails: any;
  storeCartData: any = [];
  inCart: boolean = false;
  showLoginModal: boolean = false;
  showRegisterModal: boolean = false;

  comments: { user: string; comment: string, rating: number }[] = [];
  averageRating: number = 0;

  ngOnInit(): void {

     this.commentForm = this.fb.group({
        comment:["",[Validators.required]],
          rating: [0, [Validators.required, Validators.min(1), Validators.max(5)]] // default değer 0 ve minimum 1 maksimum 5 yıldız olsun
      });


    this.getParamValue = this.route.snapshot.paramMap.get('id');

     var getVal = this.dataStorage.getCartData()

    this.storeCartData = getVal ? getVal : [];

    this.getData.productData.filter((ele: any) => {
      if (ele.pdId == this.getParamValue) {
        this.getProductDetails = ele;
      }
    });

    this.storeCartData.filter((ele: any) => {
      if (ele.pdId == this.getParamValue) {
        this.inCart = true;
      }
    });
  }
  addCart(data: any) {

       if(this.authService.isLoggedIn()){
              data['plusMinusCounter']=1;
             this.storeCartData.push(data);
              this.dataStorage.storeCartData(this.storeCartData);
                  this.router.navigate(['/cart']);
          }
      else {
        this.authService.resetAuth();
            this.showLoginModal = true;
    }

  }

    closeModal() {
     this.showLoginModal = false;
       this.showRegisterModal = false;
 }

   openRegisterModal() {
     this.showLoginModal = false;
      this.showRegisterModal = true;

   }

   loginSuccess() {
       this.showLoginModal = false;
      this.addCart(this.getProductDetails)

      }
     registerSuccess() {
      this.showRegisterModal = false;
       this.addCart(this.getProductDetails)
      }

    addComment(){
       if (this.commentForm.valid) {
             const newComment = {
             user: 'User',
           comment: this.commentForm.get('comment')?.value,
            rating :  this.commentForm.get('rating')?.value
          };
      this.comments.push(newComment);
       this.updateAverageRating();
       this.commentForm.reset();
         }
     }

      updateAverageRating(): void {
     if (this.comments.length === 0) {
          this.averageRating = 0;
        return;
     }
       const totalRating = this.comments.reduce((sum, comment) => sum + comment.rating, 0);
         this.averageRating = totalRating / this.comments.length;
       }


    setRating(rating:number):void{

      this.commentForm.get('rating')?.setValue(rating);
   }
    get starArray() {
          return Array(5).fill(0).map((x, i) => i+1)
     }


}
