import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GetDataService } from '../service/get-data.service';

@Component({
  selector: 'app-product-page',
  templateUrl: './product-page.component.html',
  styleUrls: ['./product-page.component.css'],
})
export class ProductPageComponent implements OnInit {
  getParamValue: any;
  getProductData: any = [];
  filterProductData: any = [];
  getSubCategoryOption: any = [];
  selectedSortOption: string = 'none';

  constructor(private route: ActivatedRoute, private getData: GetDataService) {}

  ngOnInit(): void {
    this.getParamValue = this.route.snapshot.paramMap.get('name');

    this.getData.productData.filter((ele: any) => {
      if (ele.pdCategory == this.getParamValue) {
        this.getProductData.push(ele);
        this.filterProductData.push(ele);
      }
    });

    this.getData.subCategorisFilterData.filter((ele: any) => {
      if (ele.categories == this.getParamValue) {
        this.getSubCategoryOption.push(ele);
      }
    });
  }

  filterSelect(data: any) {
    this.filterProductData = [];
    var getFilterValue: any = data.target.value;
    console.log(getFilterValue, 'getFilterValue');

    if (getFilterValue != 'all') {
      this.getData.productData.filter((ele: any) => {
        if (ele.pdSubCategory == getFilterValue) {
          this.filterProductData.push(ele);
        }
      });
    } else {
      this.filterProductData = this.getProductData;
    }
    this.resetSort();
  }

  sortProducts(event: any) {
    const sortOrder = event.target.value;
    if (sortOrder === 'asc') {
      this.filterProductData.sort((a: any, b: any) => a.pdPrice - b.pdPrice);
    } else if (sortOrder === 'desc') {
      this.filterProductData.sort((a: any, b: any) => b.pdPrice - a.pdPrice);
    }
  }

  resetSort() {
    this.selectedSortOption = 'none';
  }
}
<app-navbar></app-navbar>
<main class="container-fluid mt-5 mb-5">

    <div class="row pageBox">

        <div class="col-lg-4 col-12">

            <!-- filter  -->
            <p>Filter</p>
            <p>Category: {{getParamValue | titlecase}}</p>
            <p>Subcategory </p>
            <div class="col-lg-4">
                <select class="form-select form-select-sm" (change)="filterSelect($event); resetSort()">
                    <option value="all">All</option>
                    <option *ngFor="let data of getSubCategoryOption" value="{{data.subcategories}}">
                        {{data.subcategories}}</option>
                </select>
            </div>

            <!-- Price Sorting -->
            <p>Price</p>
            <div class="col-lg-4">
                <select class="form-select form-select-sm" [(ngModel)]="selectedSortOption" (change)="sortProducts($event)">
                    <option value="none">Select</option>
                    <option value="asc">Low to High</option>
                    <option value="desc">High to Low</option>
                </select>
            </div>
        </div>

        <div class="col-lg-8 col-12">
            <!-- product content  -->

            <div class="row pageContent mt-5" *ngFor="let data of filterProductData">
                <div class="col-lg-4 col-12 mt-3 ">
                    <img src="{{data.pdImg}}">
                </div>
                <div class="col-lg-8 col-12 mt-5">
                    <h4>{{data.pdName}}</h4>
                    <p>{{data.pdDesc}}</p>
                    <p><span class="fw-medium"><i class="bi bi-currency-rupee"></i></span> {{data.pdPrice | number}}</p>
                    <a class="btn btn-sm btn-dark" [routerLink]="['/product-detail',data.pdCategory,data.pdId]">view
                        product <i class="bi bi-arrow-right"></i></a>
                </div>
            </div>

        </div>

    </div>

</main>
